import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import "./button.scss";

const ShimmerButton = ({ size }) => (
  <div
    className={classNames({
      "shimmer shimmer-button": true,
      [`shimmer-button--${size}`]: true,
    })}
  />
);

ShimmerButton.propTypes = {
  size: PropTypes.oneOf(["lg", "md", "sm"]),
};

ShimmerButton.defaultProps = {
  size: "md",
};

export default ShimmerButton;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";

const ShimmerCircularImage = ({ size, center, className }) => {
  const style = {};
  if (size) {
    style.width = `${size}px`;
    style.height = `${size}px`;
  }

  return (
    <div
      className={classNames({
        "text-center": center,
      })}
    >
      <div
        style={style}
        className={classNames({
          "shimmer shimmer-avatar": true,
          [className]: className,
        })}
      ></div>
    </div>
  );
};

ShimmerCircularImage.propTypes = {
  size: PropTypes.number,
  center: PropTypes.bool,
  className: PropTypes.string,
};

ShimmerCircularImage.defaultProps = {
  center: false,
  size: 80,
};

export default ShimmerCircularImage;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";

const ShimmerThumbnail = ({
  height,
  width,
  center,
  className,
  fitOnFrame,
  rounded,
}) => {
  const style = {};
  if (height) style.height = `${height}px`;
  if (width) style.width = `${width}px`;

  return (
    <div
      className={classNames({
        "h-100": fitOnFrame,
        "text-center ": center,
      })}
    >
      <div
        className={classNames({
          "h-100": fitOnFrame,
          "shimmer shimmer-thumbnail": true,
          "border-rounded": rounded,
          [className]: className,
        })}
        style={style}
      ></div>
    </div>
  );
};

ShimmerThumbnail.propTypes = {
  height: PropTypes.number,
  width: PropTypes.number,
  center: PropTypes.bool,
  className: PropTypes.string,
  fitOnFrame: PropTypes.bool,
  rounded: PropTypes.bool,
};

ShimmerThumbnail.defaultProps = {
  canter: false,
  fitOnFrame: false,
  rounded: false,
  height: 250,
};

export default ShimmerThumbnail;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import "./title.scss";

const ShimmerTitle = ({ line, gap, variant, className }) => {
  const renderLines = () => {
    const title_lines = [];

    for (let index = 0; index < line; index++) {
      title_lines.push(
        <div className="shimmer shimmer-title-line" key={index} />
      );
    }

    return title_lines;
  };

  return (
    <div
      className={classNames({
        grid: true,
        "shimmer-title": variant === "primary",
        "shimmer-title--secondary": variant === "secondary",
        [`grid-gap-${gap}`]: gap,
        [className]: className,
      })}
    >
      {renderLines()}
    </div>
  );
};

ShimmerTitle.propTypes = {
  line: PropTypes.number,
  gap: PropTypes.oneOf([10, 15, 20, 30]),
  variant: PropTypes.oneOf(["primary", "secondary"]),
  className: PropTypes.string,
};

ShimmerTitle.defaultProps = {
  line: 2,
  gap: 10,
  variant: "primary",
};

export default ShimmerTitle;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import "./card.scss";

const ShimmerCard = ({ children, className, paddingSize }) => {
  if (!children) return null;
  return (
    <div
      className={classNames({
        "shimmer-card": true,
        [`p-${paddingSize}`]: paddingSize,
        [className]: className,
      })}
    >
      {children}
    </div>
  );
};

ShimmerCard.propTypes = {
  className: PropTypes.string,
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array])
    .isRequired,
  paddingSize: PropTypes.oneOf([false, 20, 30]),
};

ShimmerCard.defaultProps = { paddingSize: false };

export default ShimmerCard;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { ShimmerThumbnail, ShimmerCircularImage } from "../../common/Image";
import ShimmerTitle from "../../common/Title";
import ShimmerCard from "../../common/Card";

const ShimmerSimpleGallery = ({
  row,
  col,
  gap,
  className,
  fitOnFrame,
  imageType,
  imageHeight,
  caption,
  card,
}) => {
  const renderPhotos = () => {
    const photos = [];
    const items = row * col;

    for (let index = 0; index < items; index++) {
      photos.push(
        <div key={index}>
          {imageType === "thumbnail" && (
            <ShimmerThumbnail
              key={index}
              fitOnFrame={fitOnFrame}
              height={imageHeight}
              className={classNames({
                "m-0": true,
              })}
            />
          )}

          {imageType === "circular" && (
            <ShimmerCircularImage size={imageHeight} center />
          )}
          {caption && (
            <ShimmerTitle
              variant="secondary"
              className={classNames({
                "mt-15": imageType === "thumbnail",
                "w-50 ml-auto mr-auto circular-image-caption":
                  imageType === "circular",
              })}
            />
          )}
        </div>
      );
    }

    return photos;
  };

  if (!card)
    return (
      <div
        className={classNames({
          grid: true,
          [`grid-gap-${gap}`]: true,
          [`grid-column-${col}`]: true,
          [className]: className,
        })}
      >
        {renderPhotos()}
      </div>
    );

  return (
    <ShimmerCard paddingSize={20}>
      <div
        className={classNames({
          grid: true,
          [`grid-gap-${gap}`]: true,
          [`grid-column-${col}`]: true,
          [className]: className,
        })}
      >
        {renderPhotos()}
      </div>
    </ShimmerCard>
  );
};

ShimmerSimpleGallery.propTypes = {
  row: PropTypes.number,
  col: PropTypes.oneOf([2, 3, 4]),
  gap: PropTypes.oneOf([20, 30]),
  fitOnFrame: PropTypes.bool,
  imageType: PropTypes.oneOf(["thumbnail", "circular"]),
  imageHeight: PropTypes.number,
  caption: PropTypes.bool,
  card: PropTypes.bool,
};

ShimmerSimpleGallery.defaultProps = {
  row: 3,
  col: 3,
  gap: 20,
  fitOnFrame: false,
  imageType: "thumbnail",
  caption: false,
  card: false,
};

export default ShimmerSimpleGallery;
import React from "react";
import PropTypes from "prop-types";
import { ShimmerThumbnail } from "../../common/Image";
import ShimmerSimpleGallery from "./SimpleGallery";
import ShimmerCard from "../../common/Card";
import "./gallery.scss";

const ShimmerFeaturedGallery = ({ row, col, gap, card, frameHeight }) => {
  const style = {};
  if (frameHeight) style.height = `${frameHeight}px`;

  const renderContent = () => (
    <div className="shimmer-row shimmer-gallery-puzzle" style={style}>
      <div className="shimmer-col flex-1">
        <ShimmerThumbnail className="h-100" fitOnFrame />
      </div>
      <div className="shimmer-col flex-1">
        <ShimmerSimpleGallery
          row={row}
          col={col}
          gap={gap}
          className="h-100"
          fitOnFrame
        />
      </div>
    </div>
  );

  if (!card) return renderContent();

  return <ShimmerCard paddingSize={20}>{renderContent()}</ShimmerCard>;
};

ShimmerFeaturedGallery.propTypes = {
  row: PropTypes.number,
  col: PropTypes.oneOf([2, 3, 4]),
  gap: PropTypes.oneOf([20, 30]),
  card: PropTypes.bool,
  frameHeight: PropTypes.number,
};

ShimmerFeaturedGallery.defaultProps = {
  card: false,
  row: 2,
  col: 2,
  gap: 20,
  frameHeight: 600,
};

export default ShimmerFeaturedGallery;
import React from "react";

const ShimmerTableCol = () => <div className="shimmer shimmer-table-col" />;

export default ShimmerTableCol;
